    fun getAllCities(filterQuery: String = ""): List<CityTimeZoneInfo> = cities.filter {
        it.city.contains(filterQuery, ignoreCase = true) or
                it.timeZoneId.contains(filterQuery, ignoreCase = true)
    }

    fun getCityById(id: String?): CityTimeZoneInfo? = cities.find { it.id == id }

    val defaultCity: CityTimeZoneInfo
        get() {
            val currentTimeZoneId = TimeZone.getDefault().id
            return cities.find { it.timeZoneId == currentTimeZoneId }
                ?: getCityById("new-york-usa")!!
        }
}